import torch
import torch.nn as nn
import torch.nn.functional as F

class ConvBlock(nn.Module):
    def __init__(self, in_channels, out_channels, kernel_size, stride=1, padding=0):
        super(ConvBlock, self).__init__()
        self.conv = nn.Conv2d(in_channels, out_channels, kernel_size, stride, padding)
        self.bn = nn.BatchNorm2d(out_channels)
        self.act = nn.SiLU()  # Swish activation function

    def forward(self, x):
        return self.act(self.bn(self.conv(x)))

class BottleneckBlock(nn.Module):
    def __init__(self, in_channels, out_channels, shortcut=True):
        super(BottleneckBlock, self).__init__()
        self.conv1 = ConvBlock(in_channels, out_channels, kernel_size=1)
        self.conv2 = ConvBlock(out_channels, out_channels, kernel_size=3, padding=1)
        self.shortcut = shortcut

    def forward(self, x):
        y = self.conv2(self.conv1(x))
        if self.shortcut:
            y = y + x
        return y

class SPPFBlock(nn.Module):
    def __init__(self, in_channels, out_channels):
        super(SPPFBlock, self).__init__()
        self.conv = ConvBlock(in_channels, out_channels, kernel_size=1)
        self.pool = nn.MaxPool2d(kernel_size=5, stride=1, padding=2)

    def forward(self, x):
        y1 = self.pool(x)
        y2 = self.pool(y1)
        y3 = self.pool(y2)
        return self.conv(torch.cat([x, y1, y2, y3], dim=1))

class C2FBlock(nn.Module):
    def __init__(self, in_channels, out_channels, num_bottlenecks):
        super(C2FBlock, self).__init__()
        self.conv1 = ConvBlock(in_channels, out_channels, kernel_size=1)
        self.bottlenecks = nn.ModuleList([BottleneckBlock(out_channels // 2, out_channels // 2, shortcut=False) for _ in range(num_bottlenecks)])
        self.conv2 = ConvBlock(out_channels, out_channels, kernel_size=1)

    def forward(self, x):
        # Split the input tensor into two equal halves along the channel dimension
        y1, y2 = torch.split(x, x.shape[1] // 2, dim=1)  # Use torch.split for even division
        for bottleneck in self.bottlenecks:
            y2 = bottleneck(y2)
        return self.conv2(torch.cat([y1, y2], dim=1))

class YOLOv8(nn.Module):
    def __init__(self, num_classes):
        super(YOLOv8, self).__init__()
        self.backbone = nn.Sequential(
            ConvBlock(3, 64, kernel_size=3, padding=1),
            C2FBlock(64, 128, num_bottlenecks=3),
            BottleneckBlock(128, 256),
            SPPFBlock(256, 512)
        )
        self.head = nn.Sequential(
            ConvBlock(512, 256, kernel_size=3, padding=1),
            ConvBlock(256, 128, kernel_size=3, padding=1),
            nn.Conv2d(128, num_classes, kernel_size=1)
        )

    def forward(self, x):
        x = self.backbone(x)
        x = self.head(x)
        return x

# Parámetros de entrada
input_shape = (3, 640, 640)  # Canales, Altura, Anchura
num_classes = 80  # Ajustar según el problema

# Crear el modelo
model = YOLOv8(num_classes)
